package com.example.pashko00710.rssmvpreader.app;import android.app.Application;import android.content.Context;import com.example.pashko00710.rssmvpreader.di.components.DaggerITalksTEDAppComponent;import com.example.pashko00710.rssmvpreader.di.components.ITalksTEDAppComponent;import com.example.pashko00710.rssmvpreader.di.modules.TalksTEDAppModule;public class TalksTEDApp extends Application {    private ITalksTEDAppComponent appComponent;    public static TalksTEDApp get(Context context) {        return (TalksTEDApp) context.getApplicationContext();    }    @Override    public void onCreate() {        super.onCreate();        buildGraphAndInject();    }    public ITalksTEDAppComponent getAppComponent() {        return appComponent;    }    public void buildGraphAndInject() {        appComponent = DaggerITalksTEDAppComponent.builder()                .talksTEDAppModule(new TalksTEDAppModule(this))                .build();        appComponent.inject(this);    }}