package com.example.pashko00710.rssmvpreader.common;import android.app.Fragment;import com.example.pashko00710.rssmvpreader.di.IHasComponent;public abstract class BaseFragment extends Fragment {    @SuppressWarnings("unchecked")    protected <T> T getComponent(Class<T> componentType) {        return componentType.cast(((IHasComponent<T>)getActivity()).getComponent());    }}