package com.example.pashko00710.rssmvpreader.injection.modules;import com.example.pashko00710.rssmvpreader.ui.presenter.DetailsActivityPresenterImpl;import com.example.pashko00710.rssmvpreader.ui.presenter.MainActivityPresenterImpl;import com.example.pashko00710.rssmvpreader.ui.view.IDetailsActivityView;import com.example.pashko00710.rssmvpreader.ui.view.IMainActivityView;import dagger.Module;import dagger.Provides;@Modulepublic class ActivityModule {    private IMainActivityView view;    public ActivityModule(IMainActivityView view) {        this.view = view;    }    @Provides    public IMainActivityView provideView() {        return view;    }    @Provides    public MainActivityPresenterImpl provideMainActivityPresenterImpl (IMainActivityView view){        return  new MainActivityPresenterImpl(view);    }    @Provides    public DetailsActivityPresenterImpl provideDetailsActivityPresenterImpl (IDetailsActivityView view) {        return new DetailsActivityPresenterImpl(view);    }}