package com.example.pashko00710.rssmvpreader.injection.modules;import android.app.Application;import com.example.pashko00710.rssmvpreader.NewsApplication;import javax.inject.Singleton;import dagger.Module;import dagger.Provides;@Modulepublic class ApplicationModule {    private final NewsApplication app;    public ApplicationModule(NewsApplication app) {        this.app = app;    }    @Provides    @Singleton    public Application provideApplication() {        return app;    }}