package com.example.pashko00710.rssmvpreader.injection.modules;import com.example.pashko00710.rssmvpreader.ui.presenter.DetailsActivityPresenterImpl;import com.example.pashko00710.rssmvpreader.ui.view.IDetailsActivityView;import dagger.Module;import dagger.Provides;@Modulepublic class DetailsActivityModule {    private IDetailsActivityView view;    public DetailsActivityModule(IDetailsActivityView view) {        this.view = view;    }    @Provides    public IDetailsActivityView provideView() {        return view;    }    @Provides    public DetailsActivityPresenterImpl provideDetailsActivityPresenterImpl (IDetailsActivityView view) {        return new DetailsActivityPresenterImpl(view);    }}