package com.example.pashko00710.rssmvpreader.ui;import android.annotation.TargetApi;import android.os.Build;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import android.widget.ImageView;import android.widget.TextView;import com.example.pashko00710.rssmvpreader.R;import com.example.pashko00710.rssmvpreader.injection.IHasComponent;import com.example.pashko00710.rssmvpreader.injection.components.DetailsActivityComponent;import com.example.pashko00710.rssmvpreader.ui.presenter.DetailsActivityPresenterImpl;import com.example.pashko00710.rssmvpreader.ui.view.IDetailsActivityView;import com.squareup.picasso.Picasso;import javax.inject.Inject;import butterknife.BindView;import butterknife.ButterKnife;public class DetailsActivity extends AppCompatActivity implements IDetailsActivityView, IHasComponent<DetailsActivityComponent> {    @Inject    DetailsActivityPresenterImpl presenter;    @BindView(R.id.second_textview_title)    TextView titleView;    @BindView(R.id.second_textview_pubdate)    TextView dateView;    @BindView(R.id.second_textview_guid)    TextView guidView;    @BindView(R.id.second_imageview_title)    ImageView pictureView;    @BindView(R.id.second_textview_articletext)    TextView articleView;    private DetailsActivityComponent detailsActivityComponent;    String itemTitle, itemDate, itemLink, itemPicture;    @TargetApi(Build.VERSION_CODES.LOLLIPOP)    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_details);        ButterKnife.bind(this);        itemTitle = getIntent().getExtras().getString("title");        itemDate = getIntent().getExtras().getString("date");        itemLink = getIntent().getExtras().getString("link");        itemPicture = getIntent().getExtras().getString("pic");        presenter = new DetailsActivityPresenterImpl(this, itemLink, articleView);        presenter.execute();        if(itemPicture == null){            pictureView.setImageDrawable(getDrawable(R.drawable.cap));        } else {            Picasso.with(getApplicationContext()).load(itemPicture).into(pictureView);        }        titleView.setText(itemTitle);        dateView.setText(itemDate);        guidView.setText(itemLink);    }    @Override    public DetailsActivityComponent getComponent() {        return detailsActivityComponent;    }}