package com.example.pashko00710.rssmvpreader.ui;import android.annotation.TargetApi;import android.os.Build;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import android.widget.ImageView;import android.widget.TextView;import com.example.pashko00710.rssmvpreader.R;import com.example.pashko00710.rssmvpreader.injection.IHasComponent;import com.example.pashko00710.rssmvpreader.injection.components.DetailsActivityComponent;import com.example.pashko00710.rssmvpreader.ui.presenter.DetailsActivityPresenterImpl;import com.example.pashko00710.rssmvpreader.ui.view.IDetailsActivityView;import com.squareup.picasso.Picasso;import javax.inject.Inject;public class DetailsActivity extends AppCompatActivity implements IDetailsActivityView, IHasComponent<DetailsActivityComponent> {    @Inject    DetailsActivityPresenterImpl presenter;    private DetailsActivityComponent detailsActivityComponent;    String itemTitle, itemDate, itemGuid, itemPicture;    TextView articleView;    @TargetApi(Build.VERSION_CODES.LOLLIPOP)    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_details);        TextView titleView = (TextView) findViewById(R.id.second_textview_title);        TextView dateView = (TextView) findViewById(R.id.second_textview_pubdate);        TextView guidView = (TextView) findViewById(R.id.second_textview_guid);        ImageView pictureView = (ImageView) findViewById(R.id.second_imageview_title);        articleView = (TextView) findViewById(R.id.second_textview_articletext);        itemTitle = getIntent().getExtras().getString("title");        itemDate = getIntent().getExtras().getString("date");        itemGuid = getIntent().getExtras().getString("guid");        itemPicture = getIntent().getExtras().getString("pic");        presenter = new DetailsActivityPresenterImpl(this, itemGuid, articleView);        presenter.execute();        if(itemPicture == null){            pictureView.setImageDrawable(getDrawable(R.drawable.cap));        } else {            Picasso.with(getApplicationContext()).load(itemPicture).into(pictureView);        }        titleView.setText(itemTitle);        dateView.setText(itemDate);        guidView.setText(itemGuid);    }    @Override    public DetailsActivityComponent getComponent() {        return detailsActivityComponent;    }}