package com.example.pashko00710.rssmvpreader.ui.base;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import com.example.pashko00710.rssmvpreader.NewsApplication;import com.example.pashko00710.rssmvpreader.injection.components.ApplicationComponent;public abstract class BaseActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setupComponent(NewsApplication.get(this).getAppComponent());    }    protected abstract void setupComponent(ApplicationComponent appComponent);}